import dataIntro from "../home/dataIntro.vue";
import Tab from "./tab3.vue";
import Footer from "../home/footer.vue";
import "../../../node_modules/echarts/map/js/province/hubei.js";
import AreaList from "../../../public/area.js";

var hubei = require("../../../static/hubeisheng.json");

export default {
  data: function data() {
    return {
      TotalData: {},
      //省数据概览
      show: false,
      //地图切换
      showArea: false,
      innerIndex: 0,
      //地图切换索引
      nowDataArr: [],
      //现存确诊
      totalDataArr: [],
      //累计确诊
      currentConfirmedCount: [],
      //现存确诊折线图
      dateLine: [],
      //时间线
      newAdd: {},
      //新增
      total: {},
      //累计
      flag: false,
      areaList: AreaList,
      //省市联动的数据
      lineNeedData: {},
      confirmedIncrtemp: [],
      confirmedIncrtemp1: [],
      deadIncrtemp: [],
      deadIncrtemp1: [],
      curedIncrtemp: [],
      curedIncrtemp1: [],
      setlinechart: true
    };
  },
  components: {
    dataIntro: dataIntro,
    Tab: Tab,
    Footer: Footer
  },
  methods: {
    onConfirm: function onConfirm(e) {
      console.log("选择了" + e);
      this.showArea = false;
    },
    onCancel: function onCancel() {
      this.showArea = false;
    },
    //数据处理
    changeData: function changeData() {
      var _this = this;

      hubei.data.forEach(function (item) {
        _this.currentConfirmedCount.push(item.currentConfirmedCount);

        _this.dateLine.push(_this.insertStr(item.dateId.toString().slice(-4), 2, "-"));

        _this.confirmedIncrtemp.push(item.confirmedIncr);

        _this.deadIncrtemp.push(item.deadIncr);

        _this.curedIncrtemp.push(item.curedIncr);

        _this.confirmedIncrtemp1.push(item.confirmedCount);

        _this.deadIncrtemp1.push(item.deadCount);

        _this.curedIncrtemp1.push(item.curedCount);
      });
      this.lineNeedData.name = "现存确诊";
      this.lineNeedData.data = this.currentConfirmedCount;
      this.newAdd.name = ['新增确诊', '新增死亡', '新增治愈'];
      this.newAdd.data1 = this.confirmedIncrtemp;
      this.newAdd.data2 = this.curedIncrtemp;
      this.newAdd.data3 = this.deadIncrtemp;
      this.total.name = ['累计确诊', '累计死亡', '累计治愈'];
      this.total.data1 = this.confirmedIncrtemp1;
      this.total.data2 = this.curedIncrtemp1;
      this.total.data3 = this.deadIncrtemp1;
    },
    //必要的字符串修饰
    insertStr: function insertStr(soure, start, newStr) {
      return soure.slice(0, start) + newStr + soure.slice(start);
    },
    onChange: function onChange(index) {
      this.innerIndex = index;
    },
    clickActive: function clickActive(innerIndex) {
      this.$refs.provinceMap.swipeTo(innerIndex);
    },
    //获取单线折线图
    getLineChart: function getLineChart(ele, data, X) {
      var lineChart = this.$echarts.init(ele);
      lineChart.clear();
      this.setlinechart = false;
      window.onresize = lineChart.resize;
      lineChart.setOption({
        tooltip: {
          trigger: 'axis',
          formatter: "\n\t\t\t\t\t\t\t<div>{b}</div>\n\t\t\t\t\t\t\t<div>".concat(data.name, ":{c}</div>\n\t\t\t\t\t\t")
        },
        legend: {
          y: 'top',
          x: 'center',
          data: [data.name]
        },
        xAxis: {
          type: 'category',
          boundaryGap: false,
          data: X
        },
        grid: {
          left: '20%' //Y轴数值太长无法显示完全时增大这个值，

        },
        yAxis: {
          type: 'value'
        },
        series: [{
          name: data.name,
          type: 'line',
          data: data.data,
          stack: '总量',
          itemStyle: {
            normal: {
              color: '#28b7a3',
              //线条上的圆点颜色
              lineStyle: {
                color: '#28b7a3' //改变线条颜色

              }
            }
          }
        }]
      });
      window.addEventListener('resize', function () {
        // 自动渲染echarts
        lineChart.resize();
      });
    },
    //获取多线折线图
    getLinesChart: function getLinesChart(ele, data, X) {
      var lineChart = this.$echarts.init(ele);
      window.onresize = lineChart.resize;
      this.setlinechart = false;
      lineChart.setOption({
        tooltip: {
          trigger: 'axis'
        },
        legend: {
          y: 'top',
          x: 'center',
          data: data.name
        },
        xAxis: {
          type: 'category',
          boundaryGap: false,
          data: X
        },
        yAxis: {
          type: 'value'
        },
        grid: {
          left: '20%' //Y轴数值太长无法显示完全时增大这个值，

        },
        series: [{
          name: data.name[0],
          type: 'line',
          data: data.data1,
          //stack:'总量',//设置相同的属性的时候数据线会堆加
          itemStyle: {
            normal: {
              color: '#F74C31',
              //线条上的圆点颜色
              lineStyle: {
                color: '#F74C31' //改变线条颜色

              }
            }
          }
        }, {
          name: data.name[1],
          type: 'line',
          data: data.data3,
          //stack:'总量',
          itemStyle: {
            normal: {
              color: '#70cfc2',
              //线条上的圆点颜色
              lineStyle: {
                color: '#70cfc2' //改变线条颜色

              }
            }
          }
        }, {
          name: data.name[2],
          type: 'line',
          data: data.data2,
          //stack:'总量',
          itemStyle: {
            normal: {
              color: '#5D7092',
              //线条上的圆点颜色
              lineStyle: {
                color: '#5D7092' //改变线条颜色

              }
            }
          }
        }]
      });
    },
    //获取省地图
    getProvinceMap: function getProvinceMap(data, ele, kind, provinceName) {
      //要渲染的数据，渲染的容器，病例种类（累计，现存）,省份
      var myChart = this.$echarts.init(ele);
      /* window.onresize = myChart.resize; */

      myChart.setOption({
        backgroundColor: '#F7F7F7',
        //背景颜色
        title: {
          /* text:'China',
          subTitle:'china',
          color:'#000', */
          x: 'center'
        },
        //是视觉映射组件，用于进行『视觉编码』，也就是将数据映射到视觉元素（视觉通道）。
        visualMap: {
          pieces: [{
            value: 0,
            color: '#fff'
          }, {
            gte: 1,
            lte: 9,
            color: '#FAEBD2'
          }, {
            gte: 10,
            lte: 99,
            color: '#E9A188'
          }, {
            gte: 100,
            lte: 499,
            color: '#D56355'
          }, {
            gte: 500,
            lte: 999,
            color: '#BB3937'
          }, {
            gte: 1000,
            lte: 10000,
            color: '#772526'
          }, {
            gt: 10000,
            color: '#480F10'
          }],
          orient: 'horizontal',
          //设置数据点是垂直还是水平
          calculable: false,
          //是否显示拖拽用的手柄（手柄能拖拽调整选中范围）。
          inverse: false,
          textGap: 5,
          hoverLink: false,
          itemGap: 5,
          itemWidth: 10,
          //设置小方块的宽高
          itemHeight: 10,
          left: 'center',
          right: 'center',
          //show:false,//是否展示小方块等信息
          textStyle: {
            color: '#000',
            fontSize: '8',
            fontFamily: 'Microsoft YaHei',
            lineHeight: '50',
            textAlign: 'center'
          }
        },
        // 提示框，鼠标移入
        tooltip: {
          triggerOn: 'click',
          enterable: true,
          //提示框内是否可以进行点击
          show: true,
          //鼠标移入是否触发数据
          trigger: 'item',
          //出发方式
          formatter: "\n\t\t\t\t\t\t\t<div class=\"tooltip\">\n\t\t\t\t\t\t\t\t<div class=\"tooltip-left\">\n\t\t\t\t\t\t\t\t\t<div>{b}".concat(kind, ":{c}</div>\n\t\t\t\t\t\t\t\t</div>\n\t\t\t\t\t\t\t</div>\t\t\t\n\t\t\t\t\t\t"),
          textStyle: {
            lineHeight: 14
          }
        },
        series: [{
          name: '地图',
          type: 'map',
          //地图种类
          map: provinceName,
          //地图类型。
          showLegendSymbol: true,
          // 存在legend时显示
          data: data,
          itemStyle: {
            //地图区域的多边形 图形样式。
            fontSize: 8,
            emphasis: {
              //高亮状态下的样试
              label: {
                show: true
              }
            }
          },
          zoom: 1.1,
          //放大比例
          label: {
            //图形上的文本标签，可用于说明图形的一些数据信息
            show: true,
            fontSize: 7,
            fontFamily: '宋体',
            color: '#000',
            fontWeight: 'lighter'
          }
        }, {
          type: 'scatter',
          showEffectOn: 'render',
          //配置什么时候显示特效
          coordinateSystem: 'geo',
          //该系列使用的坐标系
          symbolSize: 10,
          //标记的大小
          data: [{
            name: '宜昌',
            value: [111.3, 30.7, 130]
          }],
          zlevel: 99999
        }]
      });
      /* 				window.addEventListener('resize', () => {
      					// 自动渲染echarts
      					myChart.resize();
      				}) */
    },
    //获取概览数据
    getProvinceData: function getProvinceData(name, latest) {
      var _this2 = this;

      this.$http.get('api/area', {
        params: {
          province: name,
          latest: latest
        }
      }).then(function (res) {
        console.log(res.data);
        var nowData = [];
        var totalData = [];
        _this2.TotalData = res.data.results[0];

        _this2.TotalData.cities.forEach(function (item) {
          //console.log(item)
          if (item.currentConfirmedCount > 10000 && item.cityName != '神农架林区' && item.cityName != '恩施州') {
            nowData.push({
              name: item.cityName + '市',
              value: item.currentConfirmedCount,
              label: {
                color: '#fff'
              }
            });
            totalData.push({
              name: item.cityName + '市',
              value: item.confirmedCount,
              label: {
                color: "#fff"
              }
            });
          } else if (item.cityName != '神农架林区' && item.cityName != '恩施州') {
            nowData.push({
              name: item.cityName + '市',
              value: item.currentConfirmedCount
            });
            totalData.push({
              name: item.cityName + '市',
              value: item.confirmedCount
            });
          } else if (item.cityName == '神农架林区') {
            nowData.push({
              name: item.cityName,
              value: item.currentConfirmedCount
            });
            totalData.push({
              name: item.cityName,
              value: item.confirmedCount
            });
          } else {
            nowData.push({
              name: '恩施土家族苗族自治州',
              value: item.currentConfirmedCount
            });
            totalData.push({
              name: '恩施土家族苗族自治州',
              value: item.confirmedCount
            });
          }
        });

        _this2.nowDataArr = nowData;
        _this2.totalDataArr = totalData;
        _this2.flag = true;
      }).catch(function (error) {
        _this2.$message.error('获取省数据错误！');
      });
    },
    showPopup: function showPopup() {
      this.show = true;
    },
    //省选择
    showPopup1: function showPopup1() {
      this.showArea = true;
    }
  },
  created: function created() {
    this.getProvinceData("湖北省", 0); //console.log(hubei)
  },
  mounted: function mounted() {},
  updated: function updated() {
    this.changeData();
    console.log("数据更新了！");
    this.getProvinceMap(this.nowDataArr, this.$refs.myEchart, "当前确诊", '湖北');
    this.getProvinceMap(this.totalDataArr, this.$refs.myEchart2, "累计确诊", "湖北");

    if (this.setlinechart) {
      this.getLineChart(this.$refs.lineChart1, this.lineNeedData, this.dateLine);
      this.getLinesChart(this.$refs.lineChart2, this.newAdd, this.dateLine);
      this.getLinesChart(this.$refs.lineChart3, this.total, this.dateLine);
    }
  }
};
