import "../../../node_modules/echarts/map/js/world.js";
export default {
  data: function data() {
    return {
      nowAreaData: [],
      //现存确诊人数(国家)
      totalAreaData: [],
      //总共确诊人数(国家)
      defineName: []
    };
  },
  props: {
    chinaData: Object,
    worldData: Array,
    required: true
  },
  methods: {
    onChange: function onChange(index) {
      //console.log(index)
      this.$emit('innerIndexW', index);
    },
    //数据处理
    getNowData: function getNowData() {
      var nowData = [];
      var totalData = [];
      var defineAreaName = []; //自定义地区名称
      //console.log(this.areaData)

      var _this = this;

      nowData.push({
        name: 'China',
        value: _this.chinaData.currentConfirmedCount
      });
      totalData.push({
        name: 'China',
        value: _this.chinaData.confirmedCount
      });
      this.worldData.forEach(function (item) {
        if (item.countryName == "美国") {
          nowData.push({
            name: 'United States',
            value: item.currentConfirmedCount
          });
          defineAreaName.push({
            EnglishName: item.countryEnglishName,
            ChineseName: item.countryName
          });
          totalData.push({
            name: 'United States',
            value: item.confirmedCount
          });
        } else {
          nowData.push({
            name: item.countryEnglishName,
            value: item.currentConfirmedCount
          });
          totalData.push({
            name: item.countryEnglishName,
            value: item.confirmedCount
          });
          defineAreaName.push({
            EnglishName: item.countryEnglishName,
            ChineseName: item.countryName
          });
        }
      }); //console.log(_this.chinaData.currentConfirmedCount)

      this.nowAreaData = nowData;
      this.totalAreaData = totalData;
      this.defineName = defineAreaName;
    },
    //获取世界地图
    getChinaMap: function getChinaMap(data, ele, kind) {
      //要渲染的数据，渲染的容器，病例种类（累计，现存）
      var myChart = this.$echarts.init(ele);
      window.onresize = myChart.resize;
      myChart.setOption({
        backgroundColor: '#F7F7F7',
        //背景颜色
        title: {
          /* text:'China',
          subTitle:'china',
          color:'#000', */
          x: 'center'
        },
        //是视觉映射组件，用于进行『视觉编码』，也就是将数据映射到视觉元素（视觉通道）。
        visualMap: {
          geo: {
            label: false
          },
          pieces: [{
            value: 0,
            color: '#fff'
          }, {
            gte: 1,
            lte: 9,
            color: '#FAEBD2'
          }, {
            gte: 10,
            lte: 99,
            color: '#E9A188'
          }, {
            gte: 100,
            lte: 499,
            color: '#D56355'
          }, {
            gte: 500,
            lte: 999,
            color: '#BB3937'
          }, {
            gte: 1000,
            lte: 10000,
            color: '#772526'
          }, {
            gt: 10000,
            color: '#480F10'
          }],
          orient: 'horizontal',
          //设置数据点是垂直还是水平
          calculable: false,
          //是否显示拖拽用的手柄（手柄能拖拽调整选中范围）。
          inverse: false,
          textGap: 5,
          hoverLink: false,
          itemGap: 5,
          itemWidth: 10,
          //设置小方块的宽高
          itemHeight: 10,
          left: 'center',
          right: 'center',
          //show:false,//是否展示小方块等信息
          textStyle: {
            color: '#000',
            fontSize: '8',
            fontFamily: 'Microsoft YaHei',
            lineHeight: '50',
            textAlign: 'center'
          }
        },
        // 提示框，鼠标移入
        tooltip: {
          triggerOn: 'click',
          enterable: false,
          //提示框内是否可以进行点击
          show: true,
          //鼠标移入是否触发数据
          trigger: 'item',
          //出发方式
          formatter: function formatter(params, ticket, callback) {
            //console.log(params)
            var html = "";

            if (params.data == undefined) {} else {
              return html = "\n\t\t\t\t\t\t\t\t\t<div class=\"tooltip\">\n\t\t\t\t\t\t\t\t\t\t<div class=\"tooltipLeft\">\n\t\t\t\t\t\t\t\t\t\t\t<div>".concat(params.name, "</div>\n\t\t\t\t\t\t\t\t\t\t\t<p>").concat(kind, ":").concat(params.value, "</p>\n\t\t\t\t\t\t\t\t\t\t</div>\n\t\t\t\t\t\t\t\t\t</div>\t\t\t\n\t\t\t\t\t\t\t\t  ");
            }
          },
          textStyle: {
            lineHeight: 14
          }
        },
        series: [{
          name: 'WorldMap',
          roam: true,
          //是否开启缩放
          type: 'map',
          //地图种类
          map: 'world',
          //地图类型。
          //nameMap:{'China':'中国'},
          showLegendSymbol: true,
          // 存在legend时显示
          data: data,
          itemStyle: {
            //地图区域的多边形 图形样式。
            fontSize: 8,
            emphasis: {
              //高亮状态下的样试
              label: {
                show: true
              }
            }
          },
          zoom: 1.2,
          //放大比例
          label: false
        }, {
          type: 'scatter',
          showEffectOn: 'render',
          //配置什么时候显示特效
          coordinateSystem: 'geo',
          //该系列使用的坐标系
          symbolSize: 10,
          //标记的大小
          data: [{
            name: '宜昌',
            value: [111.3, 30.7, 130]
          }],
          zlevel: 99999
        }]
      }), window.addEventListener('resize', function () {
        // 自动渲染echarts
        myChart.resize();
      });
    }
  },
  created: function created() {
    this.getNowData();
  },
  mounted: function mounted() {
    this.getChinaMap(this.nowAreaData, this.$refs.wordlMap, "现存确诊");
    this.getChinaMap(this.totalAreaData, this.$refs.worldMap2, "累计确诊");
  }
};
