import chinaMap from "./chinaMap.vue";
import innerChart from "./innerChart.vue";
import Tab from "./tab.vue";
import wordMap from "./wordMap.vue";
import TabTwo from "./tab2.vue";
import dataIntro from "../home/dataIntro.vue";
export default {
  data: function data() {
    return {
      TotalData: {},
      //全国数据概览
      areaData: [],
      //国内各省市数据，
      outSideData: [],
      //国外数据。
      flag: false,
      //条件渲染子组件,
      innerIndex: 0,
      //当前选中tab,
      innerIndex1: 0,
      //国内疫情趋势图表当前选中，
      innerIndex2: 0,
      //湖北/非湖北趋势,
      outSideIndex: 0,
      //世界地图索引
      outsideIndex1: 0,
      //国外疫情趋势索引
      outsideIndex2: 0,
      //国外重点国家索引
      worldData: [],
      //全世界数据，包括中国
      widthside: '10.28rem',
      show: false,
      //弹出层控制
      imgArr: ['https://img1.dxycdn.com/2020/0305/057/3400265513107522996-135.png', 'https://img1.dxycdn.com/2020/0305/963/3400265525992191202-135.png', 'https://img1.dxycdn.com/2020/0305/305/3400265538877093665-135.png', 'https://img1.dxycdn.com/2020/0305/029/3400265560352165146-135.png', 'https://img1.dxycdn.com/2020/0305/967/3400265573236833505-135.png'],
      //国外疫情趋势图
      imgArrOutside: ['https://img1.dxycdn.com/2020/0308/687/3400841103149410326-135.png', 'https://img1.dxycdn.com/2020/0308/285/3400841124624696750-135.png', 'https://img1.dxycdn.com/2020/0308/541/3400841143951601077-135.png'],
      //国外重点国家疫情趋势图
      imgArrimportant: ['https://img1.dxycdn.com/2020/0308/893/3400841216966047756-135.png', 'https://img1.dxycdn.com/2020/0308/792/3400841234146366703-135.png', 'https://img1.dxycdn.com/2020/0308/935/3400841255620754815-135.png', 'https://img1.dxycdn.com/2020/0308/020/3400841281390559553-135.png', 'https://img1.dxycdn.com/2020/0308/024/3400841305013329463-135.png', 'https://img1.dxycdn.com/2020/0308/956/3400841328635650352-135.png', 'https://img1.dxycdn.com/2020/0308/772/3400841401650097063-135.png', 'https://img1.dxycdn.com/2020/0308/213/3400841425271969327-135.png']
    };
  },
  props: [],
  components: {
    chinaMap: chinaMap,
    innerChart: innerChart,
    Tab: Tab,
    wordMap: wordMap,
    TabTwo: TabTwo,
    dataIntro: dataIntro
  },
  methods: {
    //展示弹出层
    showPopup: function showPopup() {
      this.show = true;
    },
    //获取全国数据概览
    getTotalData: function getTotalData() {
      var _this2 = this;

      this.$http.get('/api/overall').then(function (response) {
        //console.log(response.data.results.updateTime)
        _this2.TotalData = response.data.results[0]; //console.log(this.TotalData)
      }).catch(function (error) {
        _this2.$notify({
          type: 'danger',
          message: '获取全国数据失败！请刷新重试'
        });
      });
    },
    //获取子组件索引
    getChildIndex: function getChildIndex(innerIndexC) {
      this.innerIndex = innerIndexC;
    },
    onChange1: function onChange1(index) {
      this.innerIndex2 = index;
    },
    onChange2: function onChange2(index) {
      this.outsideIndex1 = index;
    },
    onChange3: function onChange3(index) {
      this.outsideIndex2 = index;
    },
    getIndexFunc: function getIndexFunc(index) {
      this.innerIndex1 = index;
    },
    //获取世界地图的索引
    getoutsideFuc: function getoutsideFuc(index) {
      this.outSideIndex1 = index;
    },
    pre2: function pre2(res) {
      this.$refs[res].prev();
    },
    nxt2: function nxt2(res) {
      this.$refs[res].next();
    },
    //点击切换高亮
    clickActive: function clickActive(innerIndex) {
      var _this = this;

      this.$refs.child.$refs.mapChild.swipeTo(innerIndex);
    },
    clickActive1: function clickActive1(innerIndex) {
      var _this = this;

      this.$refs.chatChild.$refs.chart1.swipeTo(innerIndex);
    },
    clickActive2: function clickActive2(index) {
      this.$refs.chart2.swipeTo(index);
    },
    clickActive3: function clickActive3(index) {
      this.$refs.childOut.$refs.wordlMap1.swipeTo(index);
    },
    clickActiveout: function clickActiveout(index, refName) {
      this.$refs[refName].swipeTo(index);
    },
    //处理国外的数据
    getOutSideTotalData: function getOutSideTotalData(key) {
      var data = 0;
      this.outSideData.forEach(function (item) {
        data += item[key];
      });
      return data;
    },
    //获取中国各省和外国总数据
    getProvince: function getProvince() {
      var _this3 = this;

      this.$http.get('/api/area').then(function (res) {
        var areadata = res.data.results;
        var nowArr = [];
        _this3.worldData = areadata;
        var outsideArr = []; //console.log(areadata)

        areadata.filter(function (item) {
          if (item.countryName == "中国") {
            nowArr.push(item);
          } else {
            outsideArr.push(item);
          }
        });
        _this3.areaData = nowArr;
        _this3.outSideData = outsideArr;
        _this3.flag = true;

        _this3.$emit('flag', _this3.flag);
      }).catch(function (error) {
        _this3.$notify({
          type: 'danger',
          message: '世界数据失败！请刷新！'
        });
      });
    }
  },
  created: function created() {
    var _this4 = this;

    //同时执行会报浏览器同源错误，不知道是数据原因还是怎么的。
    setTimeout(function () {
      _this4.getTotalData();
    }, 2000);
    this.getProvince();
  },
  mounted: function mounted() {},
  computed: {
    outsideCurrentComfirmed: function outsideCurrentComfirmed() {
      return this.getOutSideTotalData('currentConfirmedCount');
    },
    outsideComfirmed: function outsideComfirmed() {
      return this.getOutSideTotalData('confirmedCount');
    },
    outsideDead: function outsideDead() {
      return this.getOutSideTotalData('deadCount');
    },
    outsideCured: function outsideCured() {
      return this.getOutSideTotalData('curedCount');
    }
  }
};
